var Search={};Search.Search=function(){var n=$("#search-keyword").val();$("#search-result").html("");$("#search-resum").hide();$.ajax({url:localStorage.siteRoot+"/home/SearchMagazine",context:document.body,data:{Keyword:n,PublishYear:$("#search-year").val(),PublishSection:$("#search-section").val()}}).success(function(t){$("#search-resum").show();$("#search-keyword-text").html('"'+n+'"');$("#result-count").html(t.foundItem);$.each(t.result,function(n,t){$("#search-result").append(Search.GetResultRow(t))})}).error(function(){})};Search.GetResultRow=function(n){var t=$("#result-template").clone();return t.find(".article-content-part").html(Search.GetContentPart(n.content)),t.find(".publish-year").html(n.publishYear),t.find(".publish-section").html("/"+n.publishSection),t.find(".article-author").html(n.authorName),t.find(".article-title").html(n.title),t.html()};Search.GetContentPart=function(n){var t=$("#search-keyword").val(),i,r;return t.length==0?n.substring(0,250)+"...":(i="",r=n.split("."),$.each(r,function(n,r){var u=r.indexOf(t);u>0&&(i=r.replace(t,"<span class='mark-keyword'>"+t+"<\/span>")+"<\/br>")}),i)};